import React, { useState, useEffect } from "react";
import logo from "./logo.svg";
import "./App.css";
import { Login } from "./Login";
import { Register } from "./Register";

function App() {
    const [currentForm, setCurrentForm] = useState("login");
    const [error, setError] = useState(null);
    const [products, setProducts] = useState([]);
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const toggleForm = (formName) => {
        setCurrentForm(formName);
    };

    const handleLogin = async (userData) => {
        try {
            const response = await fetch("http://localhost:8080/auth/login", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(userData),
            });
            if (!response.ok) {
                throw new Error("Login failed");
            }
            setIsLoggedIn(true);
            fetchProducts(); // Fetch products after successful login
        } catch (error) {
            setError(error.message);
        }
    };

    const handleRegister = async (userData) => {
        try {
            const response = await fetch("http://localhost:8080/auth/register", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(userData),
            });
            if (!response.ok) {
                throw new Error("Registration failed");
            }
            setIsLoggedIn(true);
            fetchProducts(); // Fetch products after successful registration
        } catch (error) {
            setError(error.message);
        }
    };

    const fetchProducts = async () => {
        try {
            const response = await fetch("http://localhost:8080/product/getallproducts");
            if (!response.ok) {
                throw new Error("Failed to fetch products");
            }
            const data = await response.json();
            setProducts(data);
        } catch (error) {
            setError(error.message);
        }
    };

    useEffect(() => {
        if (isLoggedIn) {
            fetchProducts();
        }
    }, [isLoggedIn]);

    return (
        <div className="App">
            {isLoggedIn ? (
                <div>
                    <h2>Welcome User!</h2>
                    <h3>Product List</h3>
                    <ul>
                        {products.map((product) => (
                            <li key={product.id}>
                                <h3>{product.name}</h3>
                                <p>{product.shortDescription}</p>
                                <p>{product.price}</p>
                            </li>
                        ))}
                    </ul>
                </div>
            ) : (
                <>
                    {currentForm === "login" ? (
                        <Login onFormSwitch={toggleForm} onLogin={handleLogin} />
                    ) : (
                        <Register onFormSwitch={toggleForm} onRegister={handleRegister} />
                    )}
                    {error && <div>Error: {error}</div>}
                </>
            )}
        </div>
    );
}

export default App;
